@Autowired
private RedisLockRegistry redisLockRegistry;

Lock lock = redisLockRegistry.obtain(key);
boolean locked = false;
try {
  locked = lock.tryLock();
  if (!locked) {
    // 没有获取到锁的逻辑    
  }

  // 获取锁的逻辑
} finally {
  if (locked) {
    lock.unlock();
  }
}


public boolean isLock(String device) {

    Lock lock = redisLockRegistry.obtain(device);
    boolean status = false;
    try {
        status = lock.tryLock(10, TimeUnit.SECONDS);

    } catch (Exception e) {
        log.info(e.getMessage());
    }
    log.warn("status: {} <<<<<<<<<<", status);
    return status;
}


@Service
public class DistributedLockService {

    private final RedisLockRegistry redisLockRegistry;

    public DistributedLockService(RedisLockRegistry redisLockRegistry) {
        this.redisLockRegistry = redisLockRegistry;
    }

    public void processWithLock(String lockKey) {
        // 获取锁对象
        Lock lock = redisLockRegistry.obtain(lockKey);

        try {
            // 尝试获取锁，最多等待10秒，锁持有30秒
            if (lock.tryLock(10, 30, TimeUnit.SECONDS)) {
                try {
                    // 成功获取锁，执行业务逻辑
                    System.out.println("获得锁，执行业务逻辑");
                    // 模拟业务处理
                    Thread.sleep(5000);
                } finally {
                    // 释放锁
                    lock.unlock();
                    System.out.println("释放锁");
                }
            } else {
                System.out.println("获取锁失败");
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            System.out.println("线程被中断");
        }
    }
}


@Service
public class LockExamples {

    private final RedisLockRegistry redisLockRegistry;

    public LockExamples(RedisLockRegistry redisLockRegistry) {
        this.redisLockRegistry = redisLockRegistry;
    }

    // 方式1：立即尝试获取锁（不等待）
    public boolean tryLockImmediately(String key) {
        Lock lock = redisLockRegistry.obtain(key);
        return lock.tryLock();
    }

    // 方式2：带超时的尝试获取锁
    public boolean tryLockWithTimeout(String key) throws InterruptedException {
        Lock lock = redisLockRegistry.obtain(key);
        return lock.tryLock(5, TimeUnit.SECONDS); // 等待5秒
    }

    // 方式3：带超时和租期的获取锁
    public boolean tryLockWithLease(String key) throws InterruptedException {
        Lock lock = redisLockRegistry.obtain(key);
        return lock.tryLock(10, 30, TimeUnit.SECONDS); // 等待10秒，租期30秒
    }

    // 方式4：阻塞获取锁（不推荐，可能导致死锁）
    public void lockBlockingly(String key) {
        Lock lock = redisLockRegistry.obtain(key);
        lock.lock(); // 一直等待直到获取锁
        try {
            // 业务逻辑
        } finally {
            lock.unlock();
        }
    }
}


@Component
public class ScheduledTask {

    private final RedisLockRegistry redisLockRegistry;

    public ScheduledTask(RedisLockRegistry redisLockRegistry) {
        this.redisLockRegistry = redisLockRegistry;
    }

    @Scheduled(cron = "0 */5 * * * ?") // 每5分钟执行一次
    public void executeScheduledTask() {
        String lockKey = "scheduled-task-lock";
        Lock lock = redisLockRegistry.obtain(lockKey);

        try {
            if (lock.tryLock(0, 10, TimeUnit.MINUTES)) { // 不等待，锁10分钟
                try {
                    // 确保只有一个实例执行定时任务
                    System.out.println("开始执行定时任务...");
                    // 任务逻辑
                } finally {
                    lock.unlock();
                }
            } else {
                System.out.println("其他实例正在执行定时任务");
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }
}


@RestController
@RequestMapping("/api")
public class LockController {

    private final RedisLockRegistry redisLockRegistry;

    public LockController(RedisLockRegistry redisLockRegistry) {
        this.redisLockRegistry = redisLockRegistry;
    }

    @PostMapping("/process/{resourceId}")
    public ResponseEntity<String> processResource(@PathVariable String resourceId) {
        String lockKey = "resource-lock:" + resourceId;
        Lock lock = redisLockRegistry.obtain(lockKey);

        try {
            if (lock.tryLock(3, 15, TimeUnit.SECONDS)) {
                try {
                    // 处理资源
                    return ResponseEntity.ok("处理成功: " + resourceId);
                } finally {
                    lock.unlock();
                }
            } else {
                return ResponseEntity.status(HttpStatus.CONFLICT)
                    .body("资源正在被处理，请稍后重试");
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                .body("处理中断");
        }
    }
}


@Component
public class DistributedLockTemplate {

    private final RedisLockRegistry redisLockRegistry;

    public DistributedLockTemplate(RedisLockRegistry redisLockRegistry) {
        this.redisLockRegistry = redisLockRegistry;
    }

    public <T> T executeWithLock(String lockKey, Supplier<T> supplier) {
        return executeWithLock(lockKey, 5, 30, supplier);
    }

    public <T> T executeWithLock(String lockKey, long waitTime, long leaseTime, Supplier<T> supplier) {
        Lock lock = redisLockRegistry.obtain(lockKey);

        try {
            if (lock.tryLock(waitTime, leaseTime, TimeUnit.SECONDS)) {
                try {
                    return supplier.get();
                } finally {
                    lock.unlock();
                }
            } else {
                throw new RuntimeException("获取分布式锁失败: " + lockKey);
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            throw new RuntimeException("获取锁时被中断", e);
        }
    }

    // 无返回值的版本
    public void executeWithLock(String lockKey, Runnable runnable) {
        executeWithLock(lockKey, () -> {
            runnable.run();
            return null;
        });
    }
}

// 使用示例
@Service
public class BusinessService {

    private final DistributedLockTemplate lockTemplate;

    public BusinessService(DistributedLockTemplate lockTemplate) {
        this.lockTemplate = lockTemplate;
    }

    public void updateInventory(String productId, int quantity) {
        lockTemplate.executeWithLock("inventory:" + productId, () -> {
            // 更新库存逻辑
            System.out.println("更新产品 " + productId + " 的库存: " + quantity);
            return null;
        });
    }
}


package cn.netkiller.config;

import org.eclipse.paho.client.mqttv3.MqttConnectOptions;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.integration.annotation.IntegrationComponentScan;
import org.springframework.integration.annotation.MessagingGateway;
import org.springframework.integration.annotation.ServiceActivator;
import org.springframework.integration.channel.DirectChannel;
import org.springframework.integration.config.EnableIntegration;
import org.springframework.integration.mqtt.core.DefaultMqttPahoClientFactory;
import org.springframework.integration.mqtt.core.MqttPahoClientFactory;
import org.springframework.integration.mqtt.outbound.MqttPahoMessageHandler;
import org.springframework.integration.mqtt.support.MqttHeaders;
import org.springframework.messaging.MessageChannel;
import org.springframework.messaging.MessageHandler;
import org.springframework.messaging.handler.annotation.Header;

@Configuration
@EnableIntegration
@IntegrationComponentScan
public class MqttConfiguration {
    @Value("${mqtt.broker}")
    private String broker;
    @Value("${mqtt.username}")
    private String username;
    @Value("${mqtt.password}")
    private String password;

    private final int qos = 2;
    @Value("${mqtt.topic.prefix}")
    private String prefix;

    @Bean
    public MqttPahoClientFactory mqttClientFactory() {
        DefaultMqttPahoClientFactory factory = new DefaultMqttPahoClientFactory();
        MqttConnectOptions options = new MqttConnectOptions();
        options.setServerURIs(new String[]{broker});
        options.setUserName(username);
        options.setPassword(password.toCharArray());
        options.setAutomaticReconnect(true);
        options.setCleanSession(false);
        options.setKeepAliveInterval(20);
//        options.setConnectionTimeout(30000);
//        options.setExecutorServiceTimeout(30000);
        factory.setConnectionOptions(options);
        return factory;
    }

    @Bean
    @ServiceActivator(inputChannel = "mqttOutboundChannel")
    public MessageHandler mqttOutbound() {
        MqttPahoMessageHandler messageHandler = new MqttPahoMessageHandler("testClient", mqttClientFactory());
        messageHandler.setAsync(true);
        messageHandler.setDefaultTopic("testTopic");
        return messageHandler;
    }

    @Bean
    public MessageChannel mqttOutboundChannel() {
        return new DirectChannel();
    }

    @MessagingGateway(defaultRequestChannel = "mqttOutboundChannel")
    public interface MyGateway {

        void sendToMqtt(String data);

        void sendToMqtt(@Header(MqttHeaders.TOPIC) String topic, String data);

    }
}


@Autowired
private MqttConfiguration.MyGateway myGateway;

@GetMapping("/mqtt")
public Mono<String> mqtt(Principal principal) {
    myGateway.sendToMqtt("Test");
    myGateway.sendToMqtt("neo", "Test");
    return Mono.empty();
}


@MessagingGateway(defaultRequestChannel = "mqttOutboundChannel")
public interface MyGateway {

    void sendToMqtt(String data);

    void sendToMqtt(@Header(MqttHeaders.TOPIC) String topic, String data);

    void sendToMqtt(@Header(MqttHeaders.TOPIC) String topic, @Header(MqttHeaders.QOS) int qos, String payload);

    void sendToMqtt(@Header(MqttHeaders.TOPIC) String topic, @Header(MqttHeaders.QOS) int qos, byte[] payload);

}


package cn.netkiller.component;


import com.google.gson.Gson;
import lombok.Data;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;
import java.util.LinkedHashMap;
import java.util.Map;

@Component
@Slf4j
@Data
public class MqttComponent {
    private final String clientId = "netkiller-" + System.currentTimeMillis();

    private final Gson gson = new Gson();
    private final int qos = 2;
    @Value("${mqtt.topic.prefix}")
    private String prefix;

    private String broker;

    private String username;

    private String password;


    public MqttComponent() {

    }
    public MqttComponent(@Value("${mqtt.broker}") String broker, @Value("${mqtt.username}") String username, @Value("${mqtt.password}") String password) {
        this.broker = broker;
        this.username = username;
        this.password = password;
    }

    public void publish(String topic, String device, String session, String content) {
//        Thread.currentThread().setName(this.getClass().getSimpleName());
        MqttConnectionOptions options = new MqttConnectionOptions();
        options.setCleanStart(false);
//        options.setAutomaticReconnect(true);
        options.setConnectionTimeout(30);
        options.setKeepAliveInterval(20);
        MemoryPersistence persistence = new MemoryPersistence();

        try {
            if (username != null) {
                options.setUserName(username);
                options.setPassword(password.getBytes());
            }

            MqttAsyncClient client = new MqttAsyncClient(broker, clientId, persistence);
            IMqttToken token = client.connect(options);
            token.waitForCompletion(20000L);

            if (token.isComplete()) {
                log.debug("Connecting to broker: {} username: {} password: {} ", broker, username, password);
//                log.debug("Auth username: {} password: {} Connected!", username, password);
                topic = prefix.concat("/".concat(device).concat("/").concat(topic));
                String jsonString = gson.toJson(Map.of("session", session, "data", content), LinkedHashMap.class);
                byte[] payload = jsonString.getBytes();

//        if (client == null || !client.isConnected()) {
//            this.connect();
//        }

                try {
                    if (client.isConnected()) {
                        MqttMessage message = new MqttMessage(payload);
                        message.setQos(qos);
                        token = client.publish(topic, message);
                        token.waitForCompletion(30000L);
                        if (token.isComplete()) {
//                            log.info(String.format("Published topic: %s, message: %s", topic, message));
                            log.info(String.format("Publishing topic: %s, message: %s", topic, message));
                        }
                    }
                } finally {
                    client.close();
                }
            }
        } catch (MqttException e) {
            log.debug("Mqtt reason: " + e.getReasonCode() + ", cause: " + e.getCause() + ", msg: " + e.getMessage());
        }

    }

    public String topic(String prefix, String device, String service) {
        if (prefix == null) {
            prefix = this.prefix;
        }

        return String.format("%s/%s/%s", prefix, device, service);
    }

    public String message(int sequence, String session, String segment, String audio, String state) {
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss");
        LocalDateTime dateTime = LocalDateTime.now();
        String formattedDateTime = dateTime.format(formatter);
        String jsonString = gson.toJson(Map.of("sequence", sequence, "session", session, "segment", segment, "audio", audio, "time", formattedDateTime, "state", state), LinkedHashMap.class);
        return jsonString;
    }

    public String message(String session, String content) {
//        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss");
//        LocalDateTime dateTime = LocalDateTime.now();
//        String formattedDateTime = dateTime.format(formatter);
        String jsonString = gson.toJson(Map.of("session", session, "data", content), LinkedHashMap.class);
        return jsonString;
    }

    public void publish(String topic, String message) {
//        Thread.currentThread().setName(this.getClass().getSimpleName());
        MqttConnectionOptions options = new MqttConnectionOptions();
        options.setCleanStart(false);
//        options.setAutomaticReconnect(true);
        options.setConnectionTimeout(30);
        options.setKeepAliveInterval(20);
        MemoryPersistence persistence = new MemoryPersistence();

        try {
            if (username != null) {
                options.setUserName(username);
                options.setPassword(password.getBytes());
            }

            MqttAsyncClient client = new MqttAsyncClient(broker, clientId, persistence);
            IMqttToken token = client.connect(options);
            token.waitForCompletion(20000L);

            if (token.isComplete()) {
                log.debug("Connecting to broker: {} username: {} password: {} ", broker, username, password);
                byte[] payload = message.getBytes();

                try {
                    if (client.isConnected()) {
                        MqttMessage mqttMessage = new MqttMessage(payload);
                        mqttMessage.setQos(qos);
                        token = client.publish(topic, mqttMessage);
                        token.waitForCompletion(30000L);
                        if (token.isComplete()) {
                            log.info(String.format("Publishing topic: %s, message: %s", topic, mqttMessage));
                        }
                    }
                } finally {
                    client.close();
                }
            }
        } catch (MqttException e) {
            log.debug("Mqtt reason: " + e.getReasonCode() + ", cause: " + e.getCause() + ", msg: " + e.getMessage());
        }

    }

    public void publish(String topic, String session, String content) {
        this.publish(topic, this.message(session, content));
    }
}
