        http.httpBasic(httpSecurityHttpBasicConfigurer -> httpSecurityHttpBasicConfigurer.disable());
        return http.build();
    }

}


package cn.netkiller.config;

import jakarta.servlet.http.HttpServletRequest;
import lombok.extern.slf4j.Slf4j;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.authorization.AuthorizationDecision;
import org.springframework.security.config.annotation.method.configuration.EnableMethodSecurity;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.web.SecurityFilterChain;
import org.springframework.security.web.util.matcher.IpAddressMatcher;

import java.util.Arrays;
import java.util.List;

/**
 * @author Neo
 * @description Security 配置类
 * @date 2023-01-26 21:18
 */
@Configuration
@EnableWebSecurity
@EnableMethodSecurity
@Slf4j
public class WebSecurityConfig {
//
//    @Bean
//    public UserDetailsService userDetailsService() {
//        InMemoryUserDetailsManager manager = new InMemoryUserDetailsManager();
//        manager.createUser(User.withDefaultPasswordEncoder().username("user").password("password").roles("USER").build());
//        manager.createUser(User.withDefaultPasswordEncoder().username("neo").password("chen").roles("USER").build());
//        return manager;
//    }

    @Bean
    public SecurityFilterChain securityFilterChain(HttpSecurity http) throws Exception {

        List<IpAddressMatcher> allowedIps = Arrays.asList(
                new IpAddressMatcher("192.168.1.100"),
                new IpAddressMatcher("127.0.0.1"),
                new IpAddressMatcher("0:0:0:0:0:0:0:1"),
                new IpAddressMatcher("192.168.1.0/24")
        );

//        http.csrf((csrf) -> csrf.disable());
//        http.httpBasic(httpSecurityHttpBasicConfigurer -> httpSecurityHttpBasicConfigurer.disable());

        http.csrf((csrf) -> csrf.disable())
                .authorizeHttpRequests(authorize -> authorize
                        .requestMatchers("/adm/**").access((authentication, context) -> {

                            HttpServletRequest request = context.getRequest();
                            // 获取真实客户端 IP
                            String clientIp = getClientIp(request);
                            // 检查 IP 是否允许
                            boolean isAllowed = allowedIps.stream()
                                    .anyMatch(matcher -> matcher.matches(clientIp));
                            log.debug("{} {} => {}", request.getRequestURI(), clientIp, isAllowed);
                            return new AuthorizationDecision(isAllowed);
                        })
                        .anyRequest().permitAll()
                );
        return http.build();
    }

    private String getClientIp(HttpServletRequest request) {
        String ip = request.getHeader("X-Forwarded-For");
        if (ip == null || ip.isEmpty() || "unknown".equalsIgnoreCase(ip)) {
            ip = request.getHeader("Proxy-Client-IP");
        }
        if (ip == null || ip.isEmpty() || "unknown".equalsIgnoreCase(ip)) {
            ip = request.getHeader("WL-Proxy-Client-IP");
        }
        if (ip == null || ip.isEmpty() || "unknown".equalsIgnoreCase(ip)) {
            ip = request.getRemoteAddr();
        }
        if (ip != null && ip.contains(",")) {
            ip = ip.split(",")[0].trim();
        }
//        log.debug(ip);
        return ip;
    }


}


package cn.aigcsst.config;

import jakarta.servlet.http.HttpServletRequest;
import lombok.extern.slf4j.Slf4j;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.authorization.AuthorizationDecision;
import org.springframework.security.config.annotation.method.configuration.EnableMethodSecurity;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.web.SecurityFilterChain;

/**
 * @author Neo
 * @description Security 配置类
 * @date 2023-01-26 21:18
 */
@Configuration
@EnableWebSecurity
@EnableMethodSecurity
@Slf4j
public class WebSecurityConfiguration {

    @Bean
    public SecurityFilterChain securityFilterChain(HttpSecurity http) throws Exception {

        http.csrf((csrf) -> csrf.disable());

        http.authorizeHttpRequests(auth -> auth
                // 使用SpEL表达式读取Authorization头，并添加授权条件
                .requestMatchers("/adm/**").access((authentication, context) -> {
                    // 获取当前请求对象
                    HttpServletRequest request = context.getRequest();
                    // 读取Authorization头
                    String authorizationHeader = request.getHeader("Authorization");
                    log.debug("Authorization：" + authorizationHeader);

                    // 自定义授权逻辑（示例：头存在且为Bearer类型则授权通过）
                    boolean isAuthorized = authorizationHeader != null && authorizationHeader.startsWith("Bearer ");
                    return new AuthorizationDecision(isAuthorized);
                })
                .anyRequest().permitAll()
        );
        return http.build();
    }


}


 public static String token() {

        Calendar calendar = Calendar.getInstance();
//        instance.add(Calendar.SECOND, Config.Api.expired); // 这里是 token 过期时间
        calendar.add(Calendar.DATE, Config.Api.expired);
        try {
            Algorithm algorithm = Algorithm.HMAC256(Config.Api.appSecret);
            String token = JWT.create()
                    .withJWTId(Config.Api.appKey)
                    .withKeyId(Config.Api.appKey)
                    .withIssuer(Config.Api.appId)
                    .withIssuedAt(new Date())
                    .withSubject(Config.Api.subject)
                    .withAudience(Config.Api.audience)
                    .withExpiresAt(calendar.getTime())
                    .withNotBefore(new Date())
                    .sign(algorithm);

            SimpleDateFormat formatter = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
            String datetime = formatter.format(calendar.getTime());

            Log.d(TAG, "Calendar：" + datetime + " Token: " + token);
            return token;
        } catch (JWTCreationException exception) {
            Log.d(TAG, exception.getMessage());
        }
        return null;
    }


JWTVerifier verifier = JWT.require(algorithm)
                // specify an specific claim validations
                .withIssuer(business.getAppId())
                .withJWTId(business.getAppKey())
                .withSubject(business.getSubject())
                .withAudience(business.getAudience())
                .acceptLeeway(3)
                // reusable verifier instance
                .build();
