package cn.netkiller.services;

import lombok.Data;

import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.InitializingBean;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Service;

@Service
@Data
@Slf4j
public class TestService implements InitializingBean {

    private String appId;

    public TestService(@Value("${app.id}") String appId) {
        this.appId = appId;
        log.info(this.appId);
    }

    @Override
    public void afterPropertiesSet() throws Exception {
        log.info(this.appId);
    }
}


String httpUrl = "https://www.netkiller.cn:8080/article?id=9527";		
UriComponents uriComponents = UriComponentsBuilder.fromHttpUrl(httpUrl).build();

提取协议头
String scheme = uriComponents.getScheme();
// scheme = https
System.out.println("scheme = " + scheme);

获取host操作。
String host = uriComponents.getHost();
// host = felord.cn
System.out.println("host = " + host);

提取 Port 端口。

int port = uriComponents.getPort();
// port = -1
System.out.println("port = " + port);
但是很奇怪的是上面的是 -1,很多人误以为会是80。其实 Http 协议确实是80，但是java.net.URL#getPort()规定，若 URL 的实例未申明（省略）端口号，则返回值为-1。所以当返回了-1就等同于80，但是 URL 中不直接体现它们。

提取 Path 路径
String path = uriComponents.getPath();
// path = /spring-security/{article}
System.out.println("path = " + path);

提取 Query 参数

String query = uriComponents.getQuery();
// query = version=1&timestamp=123123325
System.out.println("query = " + query);
更加合理的提取方式：

MultiValueMap<String, String> queryParams = uriComponents.getQueryParams();
// queryParams = {version=[1], timestamp=[123123325]}
System.out.println("queryParams = " + queryParams);


String endpoint = "/employees";

UriComponentsBuilder uriBuilder = UriComponentsBuilder.fromPath(endpoint)
    .queryParam("param1", "value1")
    .queryParam("param2", "value2");

webClient.post()
        .uri(uriBuilder.build().toUri())
        .bodyValue(new Employee(...))
        .retrieve()
        .bodyToMono(Employee.class);

UriComponents uriComponents = UriComponentsBuilder.newInstance() .scheme("https") .host("www.netkiller.cn") .port("8080") .path("/article/{category}") .queryParam("id", "9527") .encode(StandardCharsets.UTF_8) .build();
UriComponents expand = uriComponents.expand("story");
log.info(expand.toUriString()); # https://www.netkiller.cn:8080/article/story?id=9527
UriComponents uriComponents = UriComponentsBuilder.newInstance() .scheme("https") .host("www.netkiller.cn") .port("8080") .path("/book/{chapter}/{section}") .queryParam("id", "9527") .encode(StandardCharsets.UTF_8) .build(); UriComponents expand = uriComponents.expand(Map.of("chapter", "chapter1", "section", "section2"));
log.info(expand.toUriString()); # https://www.netkiller.cn:8080/book/chapter1/section2?id=9527


import org.springframework.web.util.UriComponentsBuilder;

public class UriBuilderExample {

    public static void main(String[] args) {
        // Base URI
        String baseUri = "https://www.example.com";

        // 构建 URI
        UriComponentsBuilder builder = UriComponentsBuilder.fromUriString(baseUri);

        // 添加路径变量: https://www.example.com/users/{userId}
        builder.path("/users/{userId}");

        // 添加查询参数: https://www.example.com/users/{userId}?name=John&age=30
        builder.queryParam("name", "John")
                .queryParam("age", 30);

        // 添加片段，https://www.example.com/users/{userId}?name=John&age=30#profile
        builder.fragment("profile");

        // 构建最终的 URI，替换 url 中的路径变量：https://www.example.com/users/123?name=John&age=30#profile
        String finalUri = builder.buildAndExpand("123").toUriString();

        System.out.println("Final URI: " + finalUri);
    }
}


PathPattern pattern = new PathPatternParser().parse("/test/**");
PathContainer pathContainer = exchange.getRequest().getPath().pathWithinApplication();
if (pattern.matches(pathContainer)) {
    log.info("custom webFilter");
    return chain.filter(exchange);
}


PathPatternParser pathPatternParser = new PathPatternParser();

List<String> paths = List.of("/token", "/verifier", "/mock/*");
List<PathPattern> parsedPatterns = new ArrayList<>();

for (String path : paths) {
    PathPattern pathPattern = pathPatternParser.parse(path);
    parsedPatterns.add(pathPattern);
}

PathContainer pathContainer = exchange.getRequest().getPath().pathWithinApplication();
for (PathPattern pattern : parsedPatterns) {
    if (pattern.matches(pathContainer)) {
        System.out.println("Path " + pathContainer + " matches pattern " + pattern.getPatternString());
    }
}

return chain.filter(exchange);

section.screen:

@HttpExchange：是用于指定 HTTP 端点的通用注释。在接口级别使用时，它适用于所有方法。
@GetExchange：为 HTTP GET请求指定@HttpExchange。
@PostExchange：为 HTTP POST请求指定@HttpExchange。
@PutExchange：为 HTTP PUT请求指定@HttpExchange。
@DeleteExchange：为 HTTP DELETE请求指定@HttpExchange。
@PatchExchange：为 HTTP PATCH请求指定@HttpExchange。

section.programlisting:

package cn.netkiller.config;

import org.springframework.http.MediaType;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestHeader;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.service.annotation.GetExchange;
import org.springframework.web.service.annotation.HttpExchange;

import java.util.List;

@HttpExchange(
        url = "https://api.netkiller.cn", // 基础URL
        contentType = MediaType.APPLICATION_JSON_VALUE, // 默认Content-Type
        accept = MediaType.APPLICATION_JSON_VALUE // 默认Accept
)
public interface UserApiClient {

    @GetExchange("/actuator/info")
    String info();

}


package cn.netkiller.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.web.client.RestClient;
import org.springframework.web.client.support.RestClientAdapter;
import org.springframework.web.service.invoker.HttpServiceProxyFactory;

@Configuration
public class ClientConfiguration {

    @Bean
    public RestClient restClient() {
        return RestClient.builder()
                .baseUrl("https://api.example.com") // 全局基础URL（可覆盖接口的@HttpExchange配置）
                .defaultHeader("Content-Type", "application/json")
                .build();
    }

    生成 UserApiClient 代理对象
    @Bean
    public UserApiClient userApiClient(RestClient restClient) {
        // 创建 HttpServiceProxyFactory
        HttpServiceProxyFactory factory = HttpServiceProxyFactory.builderFor(
                RestClientAdapter.create(restClient)
        ).build();
        // 生成接口实现类
        return factory.createClient(UserApiClient.class);
    }
}


package cn.netkiller.controller.debug;

import cn.netkiller.annotation.TokenPass;
import cn.netkiller.config.UserApiClient;
import cn.netkiller.service.AiService;
import jakarta.annotation.Resource;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.resilience.annotation.Retryable;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

@RestController
@Slf4j
@TokenPass
@RequestMapping("/test")
public class TestController {

    @Resource
    private UserApiClient userApiClient;

    @GetMapping("info")
    public String info() {
        return userApiClient.info();
    }
}


