			禁止转译 json


package cn.netkiller.test;

import org.apache.commons.text.StringEscapeUtils;

public class TestJson {

	public static void main(String[] args) {
		System.out.println(StringEscapeUtils.escapeJson("{\"name\":\"Neo\"}"));
	}

}




输出


{\"name\":\"Neo\"}





