public void testThread() throws Exception {
	try {
		Thread sendmail = new Thread(new Runnable() {
			@Override
			public void run() {
				// Sendmail
				log.info("Sendmail OK");
			}
		});
		sendmail.setName("sendmail");
		sendmail.start();
	} catch (Exception e) {
		e.printStackTrace();
	}	
}
